package leet.algo.test;

import static org.junit.Assert.*;

import org.junit.Test;

import leet.algo.PalindromePartitionII;

public class TestPalindromePartitionII {

	@Test
	public void test1() {
		PalindromePartitionII pp = new PalindromePartitionII();
		assertTrue(1==pp.minCut("aab"));
	}
	
	@Test
	public void test2() {
		PalindromePartitionII pp = new PalindromePartitionII();
		assertTrue(1==pp.minCut("ab"));
	}
	
	
	@Test
	public void test3() {
		PalindromePartitionII pp = new PalindromePartitionII();
		int a = pp.minCut("");
		System.out.println("mincut:" + a);
	}
	
	
	@Test
	public void testPerf1() {
		PalindromePartitionII pp = new PalindromePartitionII();
		pp.judgePalindrom("", false);
	}
	
	@Test
	public void testPerf2() {
		PalindromePartitionII pp = new PalindromePartitionII();
		pp.judgePalindrom("", true);
	}
	
	

}
